let clone = require("clone");

/**
 * Fisherâ€“Yates shuffle
 */
module.exports = function (arr, random) {
    let k = 0;
    let temp = 0;
    try {
        random = random || Math.random;
        // deep clone
        let result = clone(arr);

        let len = result.length;
        for (let i = 0; i < len; i++) {
            k = ~~(random() * (len - 1));
            temp = result[i];
            result[i] = result[k];
            result[k] = temp;
        }
        return result;
    } catch (error) {
        console.error(error);
        return arr;
    }
}

function shuffleArray(arr, random) {
    let len = arr.length;
    for (let i = 0; i < len; i++) {
        k = ~~(random() * (len - 1));
        temp = arr[i];
        arr[i] = arr[k];
        arr[k] = temp;
    }
    return arr;
}

function shuffleMatrix(matrix, random) {
    return matrix;
}